import React, { useRef, useEffect, useState } from 'react';
import io from 'socket.io-client';
import './Whiteboard.css'; // Optional CSS for styling

const socket = io(process.env.REACT_APP_SOCKET_URL);
// Your backend server

const Whiteboard = () => {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas dimensions
    canvas.width = window.innerWidth * 0.9;
    canvas.height = window.innerHeight * 0.75;

    // Receive draw events
    socket.on('draw', ({ x, y }) => {
      drawCircle(ctx, x, y);
    });

    // Clear event from other users
    socket.on('clear', () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    });

    return () => {
      socket.off('draw');
      socket.off('clear');
    };
  }, []);

  const drawCircle = (ctx, x, y) => {
    ctx.fillStyle = '#000';
    ctx.beginPath();
    ctx.arc(x, y, 2, 0, Math.PI * 2);
    ctx.fill();
  };

  const handleMouseDown = () => setIsDrawing(true);
  const handleMouseUp = () => setIsDrawing(false);
  const handleMouseLeave = () => setIsDrawing(false);

  const handleMouseMove = (e) => {
    if (!isDrawing) return;

    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const ctx = canvas.getContext('2d');
    drawCircle(ctx, x, y);
    socket.emit('draw', { x, y });
  };

  const handleClear = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    socket.emit('clear');
  };

  return (
    <div className="whiteboard-container">
      <h2>üñçÔ∏è Real-Time Collaborative Whiteboard</h2>
      <button className="clear-btn" onClick={handleClear}>Clear Board</button>
      <canvas
        ref={canvasRef}
        className="whiteboard-canvas"
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        onMouseMove={handleMouseMove}
        onMouseLeave={handleMouseLeave}
      />
    </div>
  );
};

export default Whiteboard;
